import React, { Component } from 'react';
import ButtonLike from '../stateless/ButtonLike'


export default class User extends Component {

  state = {
    likes: 0
  }

handleClick = () => {
  this.setState({
    likes: state.likes + 1
  })
}
  render() {

    return (
        <div className="user-box">
          <img className="user-image" src={this.props.user.pic_url} alt={this.props.user.name}/>
          <span className="username">{this.props.user.name}</span>
          <ButtonLike onClick={this.handleClick} />
        </div>
    );
  }
}
