import React, { Component } from 'react';
import SearchInput from './SearchInput'
import { connect } from 'react-redux';


class Instagram extends Component {

  state = {
    searchText: ""
  }

  arrayIfy = (object) => {
    console.log(object)
    return object['data']
  }

  searchBar = (word) => {
    fetch('http://api.giphy.com/v1/gifs/search?q=' + word + '&api_key=dc6zaTOxFJmzC&rating=g')
      .then(response => response.json())
      .then(data => {this.setState({searchText: this.arrayIfy(data)})})
  }

  render() {

    return (
      <div>
      <SearchInput searchBar={this.searchBar}/>
      </div>
    );
  }
}


const mapStateToProps = state => {
  return {
    posts: state.posts,
  }
}

const mapDispatchToProps = dispatch => {
    return {
        addPost: (post) => dispatch({ type: 'ADD_POST', post}),
    };
};


export default connect(mapStateToProps, mapDispatchToProps)(Instagram)
