//     return fetch(url, {
//         method: "POST",
//         body: JSON.stringify(data),
//     })
//     .then(response => response.json());
// }

  render() {

    return (
      <form onSubmit={this.handleSubmit} className="user-form">
        <input onChange={this.handleUsername} type="text" value={this.state.username}  placeholder="Username"></input>
        <input onChange={this.handleURL} type="text" value={this.state.url}  placeholder="Image URL"></input>
        <input type='submit' value="Submit"></input>
      </form>
    );
  }
}

export default connect()(UserForm)
